    var date = new Date(property * 1000);
    var year = date.getYear() + 1900;
    const leap = new Date(year, 1, 29).getDate() === 29;
    var doy = Math.floor((date - new Date(date.getFullYear(), 0, 0)) / 1000 / 60 / 60 / 24);
